@import url('https://fonts.googleapis.com/css2?family=Plus+Jakarta+Sans:wght@300;400;500;600;700;800&display=swap');
@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
}

:root {
  --radius: 0.75rem;

  /* Core palette — deep indigo primary, warm cream background */
  --background: oklch(0.978 0.006 80);
  --foreground: oklch(0.18 0.025 265);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.18 0.025 265);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.18 0.025 265);
  --primary: oklch(0.42 0.18 270);
  --primary-foreground: oklch(0.99 0 0);
  --secondary: oklch(0.965 0.008 80);
  --secondary-foreground: oklch(0.3 0.03 265);
  --muted: oklch(0.955 0.007 80);
  --muted-foreground: oklch(0.52 0.02 265);
  --accent: oklch(0.96 0.018 270);
  --accent-foreground: oklch(0.42 0.18 270);
  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.91 0.01 80);
  --input: oklch(0.91 0.01 80);
  --ring: oklch(0.42 0.18 270 / 0.4);

  /* Charts */
  --chart-1: oklch(0.42 0.18 270);
  --chart-2: oklch(0.62 0.16 200);
  --chart-3: oklch(0.72 0.17 140);
  --chart-4: oklch(0.78 0.16 55);
  --chart-5: oklch(0.65 0.22 15);

  /* Sidebar — deep indigo dark */
  --sidebar: oklch(0.22 0.06 268);
  --sidebar-foreground: oklch(0.92 0.01 265);
  --sidebar-primary: oklch(0.6 0.18 270);
  --sidebar-primary-foreground: oklch(0.99 0 0);
  --sidebar-accent: oklch(0.30 0.07 268);
  --sidebar-accent-foreground: oklch(0.95 0.01 265);
  --sidebar-border: oklch(0.32 0.06 268);
  --sidebar-ring: oklch(0.6 0.18 270);

  /* Custom tokens */
  --font-sans: 'Plus Jakarta Sans', sans-serif;
  --gradient-primary: linear-gradient(135deg, oklch(0.42 0.18 270), oklch(0.38 0.2 290));
  --gradient-hero: linear-gradient(135deg, oklch(0.22 0.06 268) 0%, oklch(0.35 0.12 275) 100%);
  --gradient-card: linear-gradient(160deg, oklch(1 0 0) 0%, oklch(0.96 0.018 270) 100%);
  --shadow-elegant: 0 4px 24px -4px oklch(0.42 0.18 270 / 0.18);
  --shadow-card: 0 2px 12px -2px oklch(0.18 0.025 265 / 0.08);
  --transition-smooth: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
}

.dark {
  --background: oklch(0.145 0 0);
  --foreground: oklch(0.985 0 0);
  --card: oklch(0.205 0 0);
  --card-foreground: oklch(0.985 0 0);
  --popover: oklch(0.205 0 0);
  --popover-foreground: oklch(0.985 0 0);
  --primary: oklch(0.922 0 0);
  --primary-foreground: oklch(0.205 0 0);
  --secondary: oklch(0.269 0 0);
  --secondary-foreground: oklch(0.985 0 0);
  --muted: oklch(0.269 0 0);
  --muted-foreground: oklch(0.708 0 0);
  --accent: oklch(0.269 0 0);
  --accent-foreground: oklch(0.985 0 0);
  --destructive: oklch(0.704 0.191 22.216);
  --border: oklch(1 0 0 / 10%);
  --input: oklch(1 0 0 / 15%);
  --ring: oklch(0.556 0 0);
  --chart-1: oklch(0.488 0.243 264.376);
  --chart-2: oklch(0.696 0.17 162.48);
  --chart-3: oklch(0.769 0.188 70.08);
  --chart-4: oklch(0.627 0.265 303.9);
  --chart-5: oklch(0.645 0.246 16.439);
  --sidebar: oklch(0.205 0 0);
  --sidebar-foreground: oklch(0.985 0 0);
  --sidebar-primary: oklch(0.488 0.243 264.376);
  --sidebar-primary-foreground: oklch(0.985 0 0);
  --sidebar-accent: oklch(0.269 0 0);
  --sidebar-accent-foreground: oklch(0.985 0 0);
  --sidebar-border: oklch(1 0 0 / 10%);
  --sidebar-ring: oklch(0.556 0 0);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    font-family: var(--font-sans);
    -webkit-font-smoothing: antialiased;
  }
}

@layer utilities {
  .font-app { font-family: var(--font-sans); }
  .gradient-primary { background: var(--gradient-primary); }
  .gradient-hero { background: var(--gradient-hero); }
  .gradient-card { background: var(--gradient-card); }
  .shadow-elegant { box-shadow: var(--shadow-elegant); }
  .shadow-card { box-shadow: var(--shadow-card); }
  .stat-card {
    @apply bg-card rounded-2xl p-5 border border-border shadow-card;
  }
  .stat-card-hero {
    @apply rounded-2xl p-6 text-primary-foreground;
    background: var(--gradient-primary);
    box-shadow: var(--shadow-elegant);
  }
  .badge-status-geplant {
    @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-semibold;
    background: oklch(0.95 0.03 265);
    color: oklch(0.42 0.18 270);
  }
  .badge-status-aktiv {
    @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-semibold;
    background: oklch(0.94 0.06 145);
    color: oklch(0.38 0.14 145);
  }
  .badge-status-abgeschlossen {
    @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-semibold;
    background: oklch(0.93 0.02 80);
    color: oklch(0.42 0.07 80);
  }
  .badge-status-abgesagt {
    @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-semibold;
    background: oklch(0.95 0.04 25);
    color: oklch(0.5 0.18 25);
  }
  .table-row-hover {
    @apply hover:bg-accent/40 transition-smooth cursor-default;
  }
}
